import { useEffect, useState } from 'react'
import axios from 'axios'
import {pokemons} from '../assets/pokeNames.json'

export default function usePokemonSearch(query) {
    query='cha'
    console.log(pokemons)
    const [names, setNames] = useState('')
    const id=0;
    // pokemons.filter(pokemon => pokemon.name.includes(query))
    /* if(pokemons.filter(pokemon => pokemon.name.includes(query))){
        names = pokemons.filter(pokemon => pokemon.name.includes(query))
    } */
    const data = (pokemons.filter((pokemon) => pokemon.name.includes(query)))
    data.forEach(e => {
        setNames(names+e.name)
    });

  return null
}
