import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View, Image, ImageBackground, Dimensions, TouchableOpacity } from 'react-native'

import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

import { useQuery, gql } from '@apollo/client';
import { QueryResult } from './query-result';
import { backgroundColors } from '../assets/colors'
import pokeball_bg from '../assets/pokeball_bg.png'
import { translateHabitat, translateType } from '../assets/translate'

import * as Speech from 'expo-speech';


export const GET_DETALLES = gql`
query getDetalles($_eq: Int) {
  pokemon_v2_pokemonspecies(where: {id: {_eq: $_eq}}) {
    pokemon_v2_pokemonspeciesflavortexts(where: {language_id: {_eq: 7}}, limit: 1) {
      flavor_text
    }
    pokemon_v2_pokemonhabitat {
      name
    }
    pokemon_v2_pokemonshape {
      name
    }
    pokemon_v2_pokemonspeciesnames(where: {language_id: {_eq: 7}}) {
      genus
    }
    name
    pokemon_v2_pokemons_aggregate {
      nodes {
        pokemon_v2_pokemonstats {
          base_stat
          pokemon_v2_stat {
            name
          }
        }
      }
    }
  }
}
`



const PokemonDetails = ({ route, navigation }) => {
  
  console.log("Detalles")
  console.log(route.params)
  const { item, type } = route.params
  const { id, name } = item
  
  const { loading, error, data } = useQuery(GET_DETALLES, {
    variables: { "_eq": id },
  });
  
  // 
  // const genus =  data.pokemon_v2_pokemonspecies[0].pokemon_v2_pokemonspeciesnames[0].genus;
/*   const shape = data.pokemon_v2_pokemonspecies[0].pokemon_v2_pokemonshape.name;
const weight = data.pokemon_v2_pokemonspecies[0].pokemon_v2_pokemons[0].weight;
  const habitat = data.pokemon_v2_pokemonspecies[0].pokemon_v2_pokemonhabitat.name; */
  
  
  // error ?? console.log(error)
  
  const imageUri = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${id}.png`
  const types = item.pokemon_v2_pokemontypes.map(e => e.pokemon_v2_type.name)


  const speak = () => {
    Speech.speak(name + "." +
    data.pokemon_v2_pokemonspecies[0].pokemon_v2_pokemonspeciesflavortexts[0].flavor_text, {
      rate: 1.1,
      language: 'es-ES',
      pitch: 1,
      voice: "es-es-x-eed-network"
    });
  };
  if(!loading){
    speak();
    console.log(data.pokemon_v2_pokemonspecies[0])
  }


  return (
    //{ backgroundColor: backgroundColors[type] }
    <View style={[styles.screen], { backgroundColor: backgroundColors[type] }}>
      <View style={[styles.name]}>
      {/* <Text style={{ color: backgroundColors[type], fontWeight: 'bold', fontSize: 24, paddingTop: 10 }}> */}
      <TouchableOpacity
        // onPress={()=>navigation.goBack()}
        // onPress={()=>navigation.navigate("Pokemon Info", {
        //   item: item,
        //   type: type,
        //   id: id+1
        // })}
        onPress={()=>navigation.push("Pokemon Info", {
          id : id+1
        })}
        >
        <Text>Atras</Text>
      </TouchableOpacity>
      <Text style={{ fontWeight: 'bold', fontSize: 24, paddingTop: 10 }}>
          {name.charAt(0).toUpperCase() + name.slice(1)}
        </Text>
      </View>
      <View style={{ opacity: 0.15, paddingTop: 30 }}>
        <ImageBackground source={pokeball_bg} style={{
          width: 200, height: 160, alignSelf: 'flex-end'
        }}
          resizeMode='contain'
        />
      </View>
      <View style={styles.infoCard}>
        <View >
          <Image source={{ uri: imageUri }} style={styles.image} resizeMode='contain' />
        </View>
        <View style={{ flexDirection: 'row' }}>
          <View>
            {types[0] && <Text style={[styles.type, { backgroundColor: backgroundColors[types[0]] }]}> {translateType(types[0])} </Text>}
          </View>
          <View>
            {types[1] && <Text style={[styles.type, { backgroundColor: backgroundColors[types[1]] }]}> {translateType(types[1])} </Text>}
          </View>
        </View>
        
        {loading ? <Text>Cargando...</Text> :
          <Text style={{ color: backgroundColors[type], fontWeight: 'bold', fontSize: 20, padding: 18 }}>
           {data.pokemon_v2_pokemonspecies[0].pokemon_v2_pokemonspeciesnames[0].genus}
          </Text>
        }
        {loading ? <Text>POKEMON</Text> :
          <View>
            <Text selectable={true} selectionColor={'gray'}>
              {(data.pokemon_v2_pokemonspecies[0].pokemon_v2_pokemonspeciesflavortexts[0].flavor_text.replace(/\s+/g, ' ')).replace(".",".\n")}
            </Text>
            <Text style={styles.habitat}>
              Habita en {translateHabitat(data.pokemon_v2_pokemonspecies[0].pokemon_v2_pokemonhabitat.name)}
            </Text>
          </View>
        }
      </View>

    </View>
  )
}

export default PokemonDetails
const { width, height } = Dimensions.get('window');

const styles = StyleSheet.create({
  screen: {
    height: height,
    // justifyContent:'center',
    alignSelf: 'center',
    flex: 1
  },
  name: {
    paddingTop: 35,
    paddingLeft: 21
  },
  infoCard: {
    margin: 10,
    backgroundColor: '#FFFFFF',
    borderRadius: 10,
    alignItems: 'center',
    padding: 20,
    // flex:1
    // height:400
  },
  image: {
    // backgroundColor:'lightgreen',
    width: 200,
    height: 200,
    marginTop: -170

  },
  imageContainer: {
    justifyContent: 'center',
    alignSelf: 'center'
  },
  type: {
    borderRadius: 10,
    padding: 5,
    marginHorizontal: 5,
    textTransform: 'capitalize'
  },
  habitat: {
    padding: 5,
    marginHorizontal: 5,
    fontStyle: 'italic'
  }
})
