import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, TouchableOpacity, View, FlatList, TextInput } from 'react-native';

import { useQuery, gql } from '@apollo/client';
import usePokemonSearch from './usePokemonSearch';

import PokemonList from './PokemonList';

export const POKEMON = gql`
  query samplePokeAPIquery ($offset: Int) {
  pokemon_v2_pokemon(limit: 21,offset: $offset){
    name
    id
    pokemon_v2_pokemontypes {
      pokemon_v2_type {
        name
      }
    }
  }
}
`


export default function App({ navigation }) {
  
  const [offst, setOffset] = useState(0)
  const [text, onChangeText] = useState('pi')
  const loadMore = () => {
    setOffset(offst+21)
    console.log(offst)
  }

  console.log("Home")
  const { loading, error, data } = useQuery(POKEMON, {variables: { "offset": offst }});
  // console.log(data)

  const renderData = ({ item, index }) => {
    return (
      <PokemonList 
      item = { item }
      navigation={ navigation }
      />
    )
  }

  return (

    <View style={styles.container}>
      <TextInput
        value={text}
        onChangeText={newText => {onChangeText(newText);usePokemonSearch(newText)}}
        placeholder='Busca un Pokémon'
        maxLength={12}
        />
      {loading ? <Text>Cargando</Text>
        :
        <FlatList
          data={data.pokemon_v2_pokemon}
          keyExtractor={(pokemon) => pokemon.id}
          ListHeaderComponent={() => <Text style={{ fontWeight: 'bold', fontSize: 18, margin: 30 }}>Toca en un pokemon de la Pokédex y mira su información!</Text>}
          numColumns={3}
          contentContainerStyle={{ alignItems: 'center' }}
          onEndReached={loadMore}
          onEndReachedThreshold={0.2}
          renderItem={renderData}
          removeClippedSubviews={false}
        />
      }
      {/* <QueryResult error={error} loading={loading} data={data}>
          {data?.tracksForHome?.map((track, index) => (
              <TrackCard key={track.id} track={track} />
          ))}
      </QueryResult> */}
    </View>
  );
}


const styles = StyleSheet.create({
  container: {
    backgroundColor: '#F7F7F7',
    flex: 1,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
  }

});
